import React, { useState } from 'react'

const Home = () => {

  const [persondata , setpersondata] = useState("");
  const helper = async()=> {
      try {
          const res = await fetch('/getdata', {
              method: "GET",
              headers: {
                 
                  "Content-Type": "application/json"
              }
          });

          const data = await res.json();
    
          setpersondata(data.name );
          console.log(data);
          if(!res.status===200  ){
              console.log('bc');
              const error = new Error(res.error);
              throw error;
          }
      } catch (err) {
      console.log(err);
      console.log("mc");
      
  }
  }
  useEffect(()=>{
    helper();
},[])
  return (
    <>

<div style={{ height: 'calc(100vh - 66px)', display: 'flex' }}>
  <div style={{ width: '50%', height: '100%', backgroundColor: 'lightpink' }}></div>
  <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', textAlign: 'center' }}>
    <div className='name'>
      <h1>Welcome <span> {persondata}</span></h1>
      <h1>We are the MERN DEVELOPER</h1>
    </div>
  </div>
  <div style={{ width: '50%', height: '100%', backgroundColor: 'lightblue', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
  </div>
</div>
      
    </>
  )
}

export default Home
